{
  "title": "Fat Loss Beginner 30-Day Program",
  "description": "A beginner-friendly fat loss plan designed to burn calories, improve metabolism, and build consistency.",
  "price": 999,
  "duration": "30 Days",
  "planContent": {
    "overview": "High-protein, calorie-controlled meals to support fat loss and muscle recovery.",
    
    "mealPlan": {
      "guidelines": "High-protein, calorie-controlled meals to support fat loss and muscle recovery.",
      "weeklyMeals": [
        {
          "day": "1",
          "meals": {
            "breakfast": "Oats with skim milk, almonds, and banana",
            "lunch": "Grilled chicken breast, brown rice, steamed vegetables",
            "snacks": "Boiled eggs + green tea",
            "dinner": "Paneer stir fry with mixed vegetables"
          }
        },
        {
          "day": "2",
          "meals": {
            "breakfast": "Vegetable omelette + whole wheat toast",
            "lunch": "Fish curry with rice",
            "snacks": "Apple + handful of peanuts",
            "dinner": "Dal, roti, salad"
          }
        }
      ]
    },

    "exercisePlan": [
      {
        "week": 1,
        "goal": "Build full body strength and fat loss",
        "days": [
          {
            "day": "1",
            "workoutType": "Full Body",
            "exercises": [
              { "name": "Jumping Jacks", "sets": "3", "reps": "30", "rest": "60 sec" },
              { "name": "Push-ups", "sets": "3", "reps": "10", "rest": "60 sec" },
              { "name": "Squats", "sets": "3", "reps": "15", "rest": "60 sec" }
            ],
            "notes": "Do warm-up before starting."
          },
          {
            "day": "2",
            "workoutType": "Cardio + Core",
            "exercises": [
              { "name": "Brisk Walking", "sets": "1", "reps": "1", "rest": "0", "duration": "20 min" },
              { "name": "Plank", "sets": "3", "reps": "1", "rest": "60 sec", "duration": "30 sec" }
            ],
            "notes": "Maintain proper form."
          }
        ]
      }
    ],

    "extras": {
      "supplements": "Whey protein optional",
      "recoveryTips": "Stretch daily and sleep 7-8 hours",
      "lifestyleTips": "Avoid sugary drinks and stay active"
    }
  }
}
